package me.uport.sdk.demoapp

import android.os.Bundle
import android.support.v7.app.AppCompatActivity
import android.view.View
import kotlinx.android.synthetic.main.verify_jwt.*
import kotlinx.coroutines.GlobalScope
import kotlinx.coroutines.launch
import kotlinx.coroutines.withContext
import me.uport.sdk.core.ITimeProvider
import me.uport.sdk.core.UI
import me.uport.sdk.jwt.JWTTools

class VerifyJWTActivity : AppCompatActivity() {
    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.verify_jwt)

        val jwtToken = "eyJ0eXAiOiJKV1QiLCJhbGciOiJFUzI1NksifQ..C8mPCCtWlYAnroduqysXYRl5xvrOdx1r4iq3A3SmGDGZu47UGTnjiZCOrOQ8A5lZ0M9JfDpZDETCKGdJ7KUeWQ"

        jwtTokenView.text = jwtToken

        verify_btn.setOnClickListener {

            errorText.text = ""
            jwtPayload.text = ""
            progress.visibility = View.VISIBLE

            GlobalScope.launch {

                val payload = JWTTools(TimeProvider(1520366435000L)).verify(jwtToken)

                withContext(UI) {
                    jwtPayload.text = payload?.toString()
                    progress.visibility = View.INVISIBLE
                }
            }
        }
    }
}

class TimeProvider(private val currentTime: Long) : ITimeProvider {
    override fun now(): Long = currentTime

}